// nogalas-router
import { createPlaywrightRouter } from 'crawlee';
import { Page } from 'playwright';
import Turndown from 'turndown';

import { b } from "../baml_client/index.js";
import type { Business } from "../baml_client/types.js";

export const router = createPlaywrightRouter();

let first = true;

const handlePopup = async (page: Page) => {
    const popup = page.locator('#popup-widget316646-close-icon');
    try {
        await popup.waitFor({ timeout: 500 })
    } finally {
      if (await popup.isVisible()) {
        await popup.click();
      }
    }
}

router.addDefaultHandler(async ({ enqueueLinks, log, page, session }) => {
    await handlePopup(page);
    if (first) {
        session?.setCookie('wam_widgets_popup_closed_65231df9-8e76-4997-93d8-20fc764b2d56_1726460451249=true', 'https://thenogaleschamber.org/')
        log.info(`enqueueing new URLs`);
        await page.getByText('More', { exact: true }).click();

        await enqueueLinks({
            selector: '[id="more-316417"] [data-ux="NavListNested"] [role="link"]',
            strategy: 'same-origin',
            label: 'html'
        });
    }
});

const turndownService = new Turndown()

router.addHandler('html', async ({ request, page, log, pushData }) => {
    const title = await page.title();
    log.info(`${title}`, { url: request.loadedUrl });
    await handlePopup(page);

    const values = await page.locator('[data-ux="ContentCard"]').all()
    const resolved = await Promise.allSettled(values.map(v => v.innerHTML()));
    const clean = resolved
        .filter((value): value is PromiseFulfilledResult<string> => value.status === 'fulfilled')
        .map(({ value }) => turndownService.turndown(value));

    await pushData({ title, url: request.loadedUrl, md: clean; });
});
